// App.js

import React, { useState } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const Tile = ({ id, text, index, moveTile }) => {
  const [, ref] = useDrag({
    type: 'TILE',
    item: { id, index },
  });

  const [, drop] = useDrop({
    accept: 'TILE',
    hover: (draggedItem) => {
      if (draggedItem.index !== index) {
        moveTile(draggedItem.index, index);
        draggedItem.index = index;
      }
    },
  });

  return (
    <div ref={(node) => ref(drop(node))} style={{ padding: '8px', border: '1px solid #ccc', margin: '4px', cursor: 'move' }}>
      {text}
    </div>
  );
};

const TileList = ({ tiles, moveTile }) => {
  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      {tiles.map((tile, index) => (
        <Tile key={tile.id} id={tile.id} text={tile.text} index={index} moveTile={moveTile} />
      ))}
    </div>
  );
};

const App = () => {
  const [tiles, setTiles] = useState([
    { id: 1, text: 'Tile 1' },
    { id: 2, text: 'Tile 2' },
    { id: 3, text: 'Tile 3' },
    // Add more tiles as needed
  ]);

  const moveTile = (fromIndex, toIndex) => {
    const newTiles = [...tiles];
    const [movedTile] = newTiles.splice(fromIndex, 1);
    newTiles.splice(toIndex, 0, movedTile);
    setTiles(newTiles);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <TileList tiles={tiles} moveTile={moveTile} />
    </DndProvider>
  );
};

export default App;
